#define EPSILON 0.1
#define Complex vec2

Complex expc(Complex z) {
    return exp(z.x) * Complex(cos(z.y), sin(z.y));
}

Complex powcf(float a, Complex b) {
    return expc(b * log(a));
}

Complex powfc(Complex a, float b) {
    float r = length(a);
    float theta = atan(a.y, a.x);
    return expc(b * Complex(log(r), theta));
}

Complex mulc(Complex a, Complex b) {
    return Complex(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

Complex powcc(Complex a, Complex b) {
    float r = length(a);
    float theta = atan(a.y, a.x);
    return expc(mulc(b, Complex(log(r), theta)));
}

float sat(Complex x, Complex y) {
    float dist = abs(length(powcc(x, y)) - length(powcc(y, x)));
    return dist / max(fwidth(dist), 0.01);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 imag = 10.0 * iMouse.xy / iResolution.xy - 5.0;

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    for(int xi = -10; xi < 10; xi++) {
        for(int yi = -10; yi < 10; yi++) {
            vec2 realImag = vec2(float(xi), float(yi)) / 3.0;
            vec2 xy = uv * 10.0;
            float c = 1.0 / sat(Complex(xy.x, imag.x + realImag.x), Complex(xy.y, imag.y + realImag.y));
            //c = smoothstep(0.5, 1.0, c);
            c = min(c, 1.0);
            float theta = atan(realImag.y, realImag.x);
            fragColor.rgb += 0.02 * vec3(cos(theta / 2.0), sin(theta / 2.0), length(realImag) / sqrt(200.0)) * c;
        }
    }
}
