void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    pos*=0.5;
    pos+=vec2(0.5);
	float vv = pos.y*pos.y;
	vv+=sin(pos.x*3.14);
	float v = sin(sin(pos.x*16.0)+(vv+iTime*.1) * 12.0)+0.95;
	fragColor = vec4( v*1.2, .5*v, 0.5*v, 1.0 );
}
