void mainImage( out vec4 fragColor, in vec2 fragCoord ){ 

    vec2 uv = fragCoord.xy/iResolution.xy;  
    fragColor = texture(iChannel0, uv);

}

float dist(vec2 p)
{	
    vec2 q = vec2(p.x*iResolution.x/iResolution.y,p.y);
    return length(q);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){ 

    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 uvMouse = iMouse.xy/iResolution.xy; 

    vec4 col = texture(iChannel0, uv);
    float dis = dist(uv - uvMouse);
    float paint = (1. - smoothstep(dis, 0., 0.01));// * step(dis, 0.1);
    vec4 paintcol = vec4(paint);
    if(iMouse.z > 0.)
    {
        fragColor =  vec4(col) + vec4(paintcol);
    } else {
        fragColor = col;
    }
}
