#define PI 3.141592
#define  time iTime

float f(float q,float factor) {
    return abs(floor(factor*sin(q)/q)/factor);
}

float q(vec2 uv) {
      float p = f(uv.x,6.+sin(time)*2.);
      p += f(uv.y,6.+cos(time)*2.);
      return p ;
}

mat2 r(float a) { return mat2(cos(a),sin(a),-sin(a),cos(a));}

float g(vec2 uv,float off,float dir) {
    uv *= r(length(uv)/(25.+sin(time+uv.y/5.)*2.));
    uv *= r(off-dir*time/3.) ;
    float p = q(uv);
    return smoothstep(0.65,1.0,p);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  ( fragCoord -.5* iResolution.xy ) / iResolution.y;
    uv *= 40.;
    float p = (g(uv,0.,1.) + g(uv,-PI/4.,-1.)) - sqrt(g(uv,0.,1.) * g(uv,-PI/4.,-1.)) ;
    float k =  pow(length(uv),2.);

    fragColor = vec4(
        mix(vec3(0.),vec3(.9,.5,.1*k),p),
        1.0);
}
