vec4 circle(vec2 position, vec2 pCord, float radius, float ar, vec4 color) {
    vec2 aposition = vec2(position);
    aposition.x *= ar;

	float cDistance = distance(aposition, pCord);
    return vec4(smoothstep(radius , radius - ((abs(sin(iTime * 0.5))/2.0) + 1.0)/30.0, cDistance)) * color;
}

vec2 circleFn(float ad, float offset) {
	return vec2(sin(iTime + offset) / ad, cos(iTime + offset) / ad);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2( fragCoord.xy / iResolution.xy);
    float ar = iResolution.x / iResolution.y;
    vec2 mouse = vec2( iMouse.xy / iResolution.xy);

    uv.x *= ar;

    vec4 outColor = circle(vec2 (0.5,0.5), uv, 0.1, ar, vec4(1.0 , (abs(sin(iTime)/4.0)), 0.0, 1.0));
    outColor += circle(vec2 (0.5, 0.5), uv, 0.05, ar, vec4(0.0 , 1.0, 0.0, 1.0));
    outColor += circle(vec2 (0.5, 0.5), uv, 0.05, ar, vec4(1.0 , 0.0, 0.0, 1.0));


    fragColor = outColor;
}
