#define GR vec3(11,163,37)/255.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;        

    vec3 col = texture(iChannel0, uv).rgb;

    vec3 s = col - GR;

    if(abs(s.r)<.1 && abs(s.g)<.1 && abs(s.b)<.1){
    	col = vec3(1.);
    }

    fragColor = vec4(col, 1.);
}

#define RED vec3(245,73,139)/255.
//#define RED vec3(245,86,85)/255.
#define BLACK vec3(32,43,51)/255.
#define WHITE vec3(234,241,245)/255.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;          
    vec3 bcol = texture(iChannel1, uv).rgb;          

    int result = 0;    
    for(int y = -2; y<=2; y+=1){
        for(int x = -2; x<=2; x+=1){

            vec2 c = fragCoord + vec2(x, y);            
            vec2 ruv = c/iResolution.xy;

            vec3 pcol = texture(iChannel0, ruv).rgb;
            if(pcol.r == 1.){
            	result += 1;
            }

        }    
    }

    if(result >0 && result <16){
    	col = WHITE;
    } else if (result == 0) {
        col = RED;
    } else {        
        col = mix(BLACK, bcol, .95);
    }        

    fragColor = vec4(col,1.0);    
}
