void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float scale = iResolution.y/iResolution.x;
    // Draw circle
    vec2 circleCoord = vec2(uv.x - 0.5, (uv.y-0.5)*scale);
	float circle = length(circleCoord);
    circle = circle * 50.0 - 10.0 ;

    //Draw rectangular 
    vec2 rectCoord =  vec2(uv.x - 0.5, (uv.y-0.5) * scale);
    vec2 dist = abs(rectCoord) - vec2(0.1);
    float outDis = length(max(dist, 0.0));
    float inDist = min(max(dist.x, dist.y), 0.0);
    float rect =  outDis + inDist;
    rect = rect * 40.0 + 0.5;

    // Transition rectangular to circle
    float result = mix(circle, rect, sin(iTime) * 0.5 + 0.5);

    // Output to screen
    fragColor = vec4(result);
}
