#define ALGORITHM 3


vec3 cuteSort( vec3 n )
{
   float a = min(min(n.x,n.y),n.z);
   float b = max(max(n.x,n.y),n.z);
   return vec3( a, n.x+n.y+n.z-a-b, b );
}

vec3 sort3( vec3 c )
{
	c.rg = (c.r < c.g) ? c.rg : c.gr;
	c.rb = (c.r < c.b) ? c.rb : c.br;
	c.gb = (c.g < c.b) ? c.gb : c.bg;
    return c;
}

vec3 sort3bk(vec3 v)
{
	vec2 t = vec2(min(v.x, v.y), max(v.x, v.y));
	return vec3(min(t.x, v.z), min(max(v.z, t.x), t.y), max(t.y, v.z)); 
}

vec3 sort3bk_cj(vec3 v)
{
	vec4 t = vec4(min(v.x, v.y), min(v.z, v.y), max(v.x, v.y), max(v.z, v.y));
	return vec3(min(t.x, t.y), max(t.x, t.y), max(t.z, t.w));
}

void mainImage(out vec4 c,vec2 p)
{
	c.rgb = tan(p.xxy+p.xyy+iTime*vec3(11,11,11));

    #if ALGORITHM==0
  	for(int i=0; i<20000; i++) c.bgr = cuteSort(c.rgb)-vec3(.01,.0,-.01);
    #elif ALGORITHM==1
    for(int i=0; i<20000; i++) c.bgr = sort3(c.rgb) -vec3(.01,.0,-.01);
    #elif ALGORITHM==2
    for(int i=0; i<20000; i++) c.bgr = sort3bk(c.rgb)-vec3(.01,.0,-.01);
    #elif ALGORITHM==3
    for(int i=0; i<20000; i++) c.bgr = sort3bk_cj(c.rgb)-vec3(.05,.0,.01);
    #endif

}
