// thanx iq and BigWings
// learning tutorials 


#define VRN_BALL 4.5

vec2 N23(vec2 p) {
    p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
    return fract(sin(p) * 18.5453);
}

vec2 voronoi(in vec2 st)
{
    vec2 id = floor(st);
    vec2 gv = fract(st);
    vec3 mx = vec3(8.0);
    vec2 p = vec2(0.);

    for(int y =- 1; y <= 1; y ++ )
    for(int x =- 1; x <= 1; x ++ )
    {
        vec2 offset = vec2(float(x), float(y));
        vec2 p = N23(id + offset);
        vec2 r = offset - gv ;
        r += (.5 + .5 * sin(iTime*5. + 6.2831 * p));
        float d = dot(r, r);

        if (d < mx.x)
        mx = vec3(d, p);

    }

    // balls molecules?
    return vec2(sqrt(mx.x)*VRN_BALL, length(mx.yz)); //balls

    // germs?
    //return vec2(sqrt(mx.x)*VRN_BALL, length(p)); // germs

    // sun hydrogen fusion-fission?
    //return vec2(sqrt(mx.x)*VRN_BALL, log(1. + .1*dot(mx.xz,mx.xz)));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);
    // just zoomng/scaling the uv
    vec2 st = (4.5 + 2.0 * sin(0.2 * iTime)) * uv ;

    vec2 c = voronoi(st);

    float d = c.x ;
    float cel_id = c.y;

    col = 0.5 + 0.5 * cos(cel_id * 6.2831 + vec3(0.0, 1.0, 2.0));
    col *= clamp(1.0 - 0.2 * d * d, 0.0, 1.0);

    fragColor = vec4(col, 1.0);
}
