void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 0.5 - fragCoord.xy/iResolution.x;
    vec2 u = uv;
    u.y-= 0.1;
    float t = iTime * -0.1;
    if(u.x < .0){
        u.y = .5 - u.y;
    }
    vec3 a = vec3(255., 255., 225. );
    a *= 1.0 - uv.y;
    vec3 rot = vec3(255., .0, 91. );
    float d = length(u)*4.0;
    float c = fract(t + d );

    vec3 q = mix(a,rot,c)/255.0;
    if(uv.x > uv.y){
        q *= vec3(50.,100.,220.)/255.;
    }
    if(uv.x > uv.y){
       float cc = length(uv-vec2(.5,.5));        
       // float cc = (uv.x  + uv.y) - .84;
        q += smoothstep(cc, cc * 1.02, .2);
    }
    fragColor = vec4(q,1.0);
}
