void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    	int c =   int(float(iFrame) +10000.*fragCoord . x * fragCoord . y) % 3 ;
	if ( c == 0 ) {
		fragColor = vec4( 1 , 0 , 0 , 1 ) ;
	} else if ( c == 1 ) {
		fragColor = vec4( 0 , 1 , 0 , 1 ) ;
	} else {
		fragColor = vec4( 0 , 0 , 1 , 1 ) ;
	}
}
