void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 offset0 = vec2(iTime, iTime) / 30.0;
    vec2 offset1 = vec2(iTime, -iTime) / 20.0;

    vec4 c0 = texture(iChannel0, uv + offset0);
    vec4 c1 = texture(iChannel0, uv * 3.0 + offset1);

    c0.r *= 1.7;
    c1.r *= 1.7;
    c0.g *= 0.6;
    c1.g *= 0.6;
    c0.b *= 0.6;
    c1.b *= 0.6;

    vec4 color = mix(c0, c1, 0.5);

    if( c0.g + c1.g > 0.65 )
        color *= 2.0;

    fragColor = color;
}
