float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;


    // Time varying pixel color
    vec4 colour = texture(iChannel0, fragCoord.xy/iResolution.xy);
    colour.a = 1.;

    float rad = distance(uv, vec2(0.0));
    //open/close
    rad += sin(iTime)*2.;
    //ripple/rip
    rad += (sin(abs(uv.y)+(pow(abs(uv.x),0.1)))-.5);

    if (rad<.4 + rand(vec2(uv.y, .1))*.02){
        float br = dot(colour.rbg, vec3(1.0));
        colour.rgb *= smoothstep(br, rad/2., abs(sin(iTime)))*2.;
    }else if (rad<.41+ rand(vec2(uv.y, .4))*.01){
        colour.rgb = vec3(0.,rand(uv),1.);
    }

    // Output to screen
    fragColor = vec4(colour);
}
