//TRASHTRASH is Josh deLorimier
//learned polar texture mapping playing with the carving idea more for today
#define MAX_STEPS 128
#define MAX_DIST 8.
#define SURF_DIST 0.001

//simplex noise from Patricio Gonzalez Vivo
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0);} 

float snoise(vec2 v) {
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}
//mercury------------------- 
float vmax(vec3 v) {
	return max(max(v.x, v.y), v.z);
}

float box(vec3 p, vec3 b) {
    return vmax(abs(p)-b);
}
//--------------------------

float map(vec3 p) {
    vec2 uv = vec2(p.x*0.9+(iTime/2.),p.y);
    vec3 tex = vec3(texture(iChannel0, uv));
	float s = sin((p.x*8.)+iTime/0.7)*sin(p.y*abs(sin(8.+iTime)*3.)+iTime)*sin(iTime+0.2);
    float s1 = sin((p.x*4.392+(iTime*0.001))+iTime);
 	float bb = box(p-vec3(0.,0.,3.), vec3(2.,1.5,1.));
    float ss = length(p-vec3(0.,(s*s1+s)*0.5,2.))-.8;
   	float b = max(-ss, bb);
    float c = max(-ss, b);
    c+=length(tex*0.02)*2.;
    return c;
}

float RM(vec3 ro, vec3 rd) {
 	float t = 0.;
    for(int i = 0;i<MAX_STEPS;i++) {
        vec3 p = ro+rd*t;
        float s = map(p);

        if(t > MAX_DIST || abs(s) < (SURF_DIST)) break;
        t+=s;

      }
   return t;
}


vec3 norm(vec3 p) {
 	float d = map(p);
    vec2 e = vec2(0.001, 0.);
    vec3 n = d - vec3(map(p-e.xyy), map(p-e.yxy), map(p-e.yyx));
    return normalize(n);
}

float shade(vec3 p, vec3 rd) {
 	vec3 lp = vec3(4,3.,-15.);
    vec3 l = normalize(lp -p);
    vec3 n = norm(p);
    float dif = clamp(dot(n,l),0.,.6);
  	float fr = pow(1.0+dot(n, rd),4.0);
   	float ao = (1.0 - fr);
    float sp = pow(max(dot(reflect(-l, n),-rd),0.0),80.);
   	return sp + max(fr,0.4)+dif*ao;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord -0.5 * iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0., 0., 0.);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.));
    vec3 col = vec3(0.);
    vec3 c1 = vec3(.5,0.2,0.);
    float d = RM(ro, rd);
    if(d<MAX_DIST) {
      vec3 p = ro + rd *d;
      float n4 = snoise(uv.xy+iTime); 
   	  float dif = shade(p, rd);
   	  col = vec3(dif)*c1;
    }
    col = pow(col, vec3(.4545));
    fragColor = vec4(col,1.0);
}
