float random (in vec2 _uv){
    return fract(sin(dot(_uv,vec2(0.4928,0.8387)))*23945.348);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= vec2(38.,floor(10.*sin(iTime*0.1)));

    vec2 ipos = floor(uv);
    vec2 fpos = fract(uv);

    uv += vec2(iTime*random(vec2(ipos.y+random(vec2(10.))*random(vec2(5.))))*20. * sin(ipos.y + 2.) - sin(iTime*0.1-ipos.y*random(vec2(ipos.y,0.0))),0.0);

    //buildings
    uv.y += random(ipos) * ipos.y;


    ipos = floor(uv);
    fpos = fract(uv);

    vec3 col = vec3(0.0);

    col = vec3(clamp(pow(random(ipos),2.),0.,1.));
    //col = vec3(fpos,1.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
