vec3 theSun(vec2 uv){
	float sun =  distance(uv, iMouse.xy / iResolution.y );
    vec4 theSun = vec4(.25,0.2,0.8,0.6) / sun;
 //uv *= mat2(1,-100,10,1) * 0.2;

   return vec3(theSun);
} 



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec3 sun = theSun(uv);

	fragColor = vec4(sun,0);    
}
