vec3 color = vec3(1);
float getDist (vec3 p ){
vec4 s = vec4 (0. ,1. ,6. ,1.);
//  s.xz  *= rot (time);
float sphereDist =length (p-s.xyz)-s.w
-sin (p.x)*3.;
float planeDist = p.y;
float d = min ( planeDist ,sphereDist );
    if(d == planeDist){ color = vec3(0,0,1);}
    else{color = vec3 (1,0,0);}
return d;
}
  float rayMarch (vec3 ro , vec3 rd){
float  d = 0.0;
for ( int i = 0 ; i < 100 ; i++){
  vec3 p = ro + rd * d;
  float ds = getDist(p);
  d += ds;
    if ( d > 100. || ds < 0.0000001){//1./iTime){
  break;
  }}
return d;
}
vec3 getNormal (vec3 p){
float d = getDist(p);
vec2 e = vec2 (.01 , 0. );

vec3 n = d-vec3 (
getDist(p-e.xyy) ,
getDist(p-e.yxy) ,
getDist(p-e.yyx) );
return normalize (n);
}

  float getLight(vec3 p){
  vec3 lightPos = vec3 (0 , 100. , 6);
lightPos.xz += 50. * vec2  (sin (iTime),cos (iTime));
  vec3 l = normalize (lightPos-p);
  vec3 n = getNormal (p);
  float dif = max (dot (l,n),0.);
    float d = rayMarch (p+n*iTime, l);
    if (d < length (lightPos-p)) dif *= .1;
  return dif;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   float ratio = iResolution.y / iResolution.x;
vec2 uv = (fragCoord.xy  / iResolution.x)
- vec2 (0.5 , 0.5 *  ratio);

    vec3 ro = vec3 (0. ,1. ,-10.);
    vec3 rd = normalize (vec3 (uv.xy ,1.));


    float d = rayMarch (ro ,rd);

    vec3 p= ro + rd * d;
    float dif = getLight (p);

  vec3  col = vec3 (dif*color);


	fragColor = vec4(col, 1.0 );
}
