const float gamma = 2.2;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 blend;

    if (iMouse.z > .5) {
	    blend = iMouse.xy / iResolution.xy;
    }
    else {
        float t = iTime * 0.25;
        blend = vec2(sin(t), cos(t));
        blend = .5 + .5 * blend / max(abs(blend.x), abs(blend.y));
    }
    float sum = blend.x + blend.y;
    if (sum > 1.) {
        blend /= sum;
    }

    vec3 a = texture(iChannel0, uv).rgb;
    vec3 b = texture(iChannel1, uv).rgb;
    vec3 c;

    if (uv.x >= .5) {
        vec3 invGamma = vec3(1.0 / gamma);
        a = pow(a, invGamma);
        b = pow(b, invGamma);
    }

    c = blend.x * a + blend.y * b;

    if (uv.x >= .5) {
        c = pow(c, vec3(gamma));
    }

    fragColor = vec4(c, 1.);
}
