float addAlpha(float a1, float a2){
	return a1 + a2 - a1 * a2;
}

float getColor0(float x0, float t, float omiga){
	float xt = omiga * t;
    float xt0 = omiga * (t - 0.1);
    float pi2 = 3.1415926 * 2.0;
    float n = floor((xt - x0)/pi2);
    float factor = 1.0 / (xt - xt0);
    float xx = n * pi2 + x0;
    float color = 0.0;
    float f = 0.0;
    while(xx > xt0 && xx >= x0){
    	f = factor * (xx - xt0);
        color = addAlpha(color , f * f);
        if(color >= 1.0){
            color = 1.0;
            break;
        }
        xx = xx - pi2;
    }
    return color;
}

float getColor(float x, float y, float t, float omiga, float m){
    float PI = 3.1415926;
	float x0 = atan(y, x) + PI;
    float step = PI * 2.0 / m;
    float color = 0.0;
    for(float i = 0.0; i < m; i = i + 1.0){
        color = addAlpha(color , getColor0(x0 + i * step, t, omiga));
        if(color >= 1.0){
            color = 1.0;
            break;
        }
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float PI = 3.1415926;
    float rot = 3.0;
    float omiga = 2.0 * PI * rot;
    float t = iTime;
    float x = uv.x - 0.5;
    float y = uv.y - 0.5;
    float r = 0.5;

    float color = getColor(x, y, t, omiga, 3.0);

    // Output to screen
    fragColor = vec4(color, 0.0, 0.0,1.0);
}
