//GEORGIY CHIPUNOV, NICE BACKGROUND

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c = fragColor;
   //   c.w = length(p = p/iResolution.y - .5);
 //

   // c = texture( iChannel0, vec2(atan(p.y,p.x), .3/c.w)+iTime )*c.w;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,0,1));

    if(fragCoord.x > 0.5 )
    {
    col.r = smoothstep(c.w,iTime,iResolution.y);

    }
    // Output to screen
    float fNumPixelsX = iResolution.x ;
    float fNumPixelsY = iResolution.y ;

    if(fragCoord.x > 555.0 && fragCoord.y > 200.0 )
    {
    col.r = smoothstep(c.w,iTime,iResolution.y);
    fragColor.b = fragCoord.x;
      fragColor.g = fragCoord.y;
    }
    else if(fragCoord.x > (fNumPixelsX/2.0 )+100.0)
    {
    col.r = smoothstep(c.w,iTime,iResolution.y);
    fragColor.r = fragCoord.x;
      fragColor.g = fragCoord.y;
    }
    // ADD MORE ELSE  IF FOR MORE LAYERS

