   // fragColor.b = fragCoord.x;
   //   fragColor.g = fragCoord.y;
   //     fragColor = vec4(col,1.0);
  //  }
    //
    // EXAMPLE 2 _OVERRIDE!
  //  if(fragCoord.x >  (fNumPixelsX-50.0) )
  //  {
  //  col.r = smoothstep(c.w,iTime,iResolution.y);
  //  fragColor.b = fragCoord.x;
  //    fragColor.g = fragCoord.y;
  //    //  fragColor = vec4(col,1.0);
  //  }



    else
    {
    fragColor = vec4(col,1.0);
    }
}
