float hash21(vec2 p) {
    p = fract(p * vec2(233.34, 851.74));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}


vec2 rot(vec2 uv,float a){
    return uv * mat2(sin(a),-sin(a),sin(a),cos(a));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 6.0;
    uv += 0.5;


    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);

	float n = hash21(id);

    n = floor(n*4.0);
    n =  mod(floor(iTime*2.0)+n,4.0);

    if (n==1.0){
        gv.x *= -1.0;
    } else if (n==2.0) {
        gv.y *= -1.0;
    } else if (n==3.0) {
        gv.x *= -1.0;
        gv.y *= -1.0;
    }



    float m = smoothstep(gv.x,gv.x + 0.01,gv.y);

    vec3 col = vec3(m);

    // Output to screen
    fragColor = vec4(col,1.0);
}
