void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / iResolution.x;

    vec3 col;
    for (int i = 0; i < 3; i++) {
        float time = iTime - .2 * float(i);
        vec2 uv_ = uv - vec2(sin(time),cos(time))*.2;

        vec2 px = fragCoord - (iResolution.xy/2.);
        vec3 col_ = 0.5 + 0.5 * cos(/*time + */length(uv_) * 2. + uv_.xyx + vec3(0,2,4) );

        px *= length(uv_);
        float squareSize = iResolution.y * .1;
        if (mod(floor(px / squareSize), vec2(2.)) == vec2(0.)
           || mod(floor(px / squareSize), vec2(2.)) == vec2(1.)) {
            col_ = 1.-col_;
        }
        col[i] = col_[i];
    }

    fragColor = vec4(col,1.0);
}
