void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;
    vec2 uvOld = vec2(uv.x,uv.y);
    uv *= 50.0;

    float time0 = iTime + fragCoord.x/iResolution.x*10.0;
    uv += 5.0*vec2(cos(time0), sin(time0));

    float top = 35.5;
    float bottom = 15.5;

    float x = uv.x;
    float y = uv.y;

    vec3 col = vec3(0,0,0);;

    float x0 = x-round(x);
    float y0 = y-round(y);
    vec2 pos0 = vec2(x0,y0);

    vec2 center = vec2(0,0);


    if(y<top && y>bottom){
    	if(distance(pos0,center)<0.2){

        	col = vec3(uvOld.x,uvOld.y,1.0);
    	}
    }



    fragColor = vec4(col,1.0);
}
