void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = 3.141592654;
    fragColor = vec4(0.0, 0.0, 0.0, 0.0);
	vec2 p = vec2(fragCoord.x, fragCoord.y);
    vec2 res = vec2(iResolution.x, iResolution.y);
    vec2 c = vec2(res.x / 2.0, res.y / 2.0);
    float t = 50.0*cos(iTime/2.0);

    float dist = sqrt(pow((c.x-p.x),2.0) + pow((c.y-p.y),2.0));

    vec2 a = vec2(p.x-c.x, c.y-p.y);
    vec2 b = vec2(1, 0);

    float deg = acos(dot(a,b) / (length(a) * length(b)) );

    if(a.y>0.0)
    {
        deg = 2.0 * pi - deg;
    }

    float q = mod(floor(dist/50.0),2.0);

    if(mod(floor(deg/(2.0*pi)*t),2.0)==q)
    {
        fragColor = vec4(0.5,0,0,1);
    }

}
