void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float music = -texture(iChannel0, vec2(0.0,0.0)).r / 6.0;

	uv *= 70.0 * (music * 0.9) ;
    uv *= atan(uv);
    uv.y *= 2.0;

    uv.x += cos(iTime);

    //PLAY AROUN WITH THIS LINES UNCOMMENTING EACH LINES ALONE ( 17, 20, 23, 26 )

    /****X-PERIENCE****/
   	vec2 gv = fract(uv) - uv.y;

    /****GATES****/
    //vec2 gv = fract(uv) - uv.y * uv.x;

    /****SPIDER****/ 
   	//vec2 gv = fract(uv) - uv.y * uv.x * uv.y * sin(uv.x - music * 10.0);

    /***TESSERACT****/
	//vec2 gv = fract(uv) - uv.y * uv.x * uv.y * sin(uv.x - music * 100.0) / tan(uv.y);


    vec2 id = floor(uv);

    gv.y +=  music * id.x  / gv.x * gv.x * gv.x;
    gv.x +=  music * gv.x / music * (5.0 * 3. * sin( music * iTime)) + gv.y;

    float d = length(gv);
    float m = (.9 + music) / d;

    vec3 col = vec3(m);

	col += pow(m, id.y) * id.y;   

    col.rgb *= vec3(sin(iTime), 0.2, cos((iTime / 50.0) * music ));

    //if(gv.x > .48 || gv.y> .48) col = vec3(1,0,0);
    //col = vec3(music + 0.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
