//#define _abs_

vec4 simu(sampler2D sam, vec2 g, vec2 s, int i, vec4 m, sampler2D kbd )
{
    if (i < 1 || texelFetch(kbd, ivec2(32,1),0).x > 0.)
	{
        vec2 gs = g/s*2.-1.;
        gs.x *= s.x/s.y;

#ifdef _abs_
        gs.y+=1.0;
#endif

        if (length(gs)<0.1)
		{
        	return vec4(0,1,0,1);
        }
        else
        {
        	return vec4(1,0,0,1);
        }
	}
	else
	{
        if (m.z > 0.)
		{
			if (length(g - m.xy) < 100.)
			{
				return vec4(0,1,0,1);
			}
		}

		vec4 c = texture(sam, fract(g/s));

		vec4 l = texture(sam, fract((g+vec2(-1,0))/s)) * 0.2;
		vec4 r = texture(sam, fract((g+vec2(1,0))/s)) * 0.2;
		vec4 b = texture(sam, fract((g+vec2(0,-1))/s)) * 0.2;
		vec4 t = texture(sam, fract((g+vec2(0,1))/s)) * 0.2;

        vec4 lb = texture(sam, fract((g+vec2(-1,-1))/s)) * 0.05;
		vec4 rb = texture(sam, fract((g+vec2(1,-1))/s)) * 0.05;
		vec4 lt = texture(sam, fract((g+vec2(-1,1))/s)) * 0.05;
		vec4 rt = texture(sam, fract((g+vec2(1,1))/s)) * 0.05;

		vec4 lp = l+r+b+t + lb+rb+lt+rt - c;

        vec2 gs = g/s*2.-1.;
        gs.x *= s.x/s.y;

#ifdef _abs_
        gs.y+=1.0;
		float feedRate = 0.05 * abs(atan(gs.x,gs.y));
		float killRate = 0.025 * length(gs*2.);
#else
		float feedRate = 0.05 * (atan(gs.x,gs.y)/3.14159*0.5+0.5)*0.5;
		float killRate = 0.025 * length(gs*3.);
#endif

		float reac = c.r * c.g * c.g;
		float u = 0.2097 * lp.r - reac + feedRate * (1.0 - c.r);
        float v = 0.105 * lp.g + reac - (feedRate + killRate) * c.g;
        vec2 sb = c.rg + vec2(u, v);

        return vec4(clamp(sb,0.,1e2),0,1);
	}

    return vec4(0);
}

void mainImage( out vec4 fragColor, vec2 g )
{
    fragColor = simu(iChannel0, g, iResolution.xy, iFrame, iMouse, iChannel1);
}

