// Golfing "Brute Force Box Filter" by removing a for loop
// Hard coded to super sample a pixel 100 times!
// 163 chars - Fabrice removed 11 chars by MAGIC!
void mainImage(out vec4 O, vec2 U)
{
    O*=0.;
    for( vec2 R = iResolution.xy, u;
       O.a++ < 1e2;
       O += max( u = fract(3.*u + .3*iTime), u.x-u) .y / 1e2 )
    u *= R.y / dot( u = U-.5*R + fract(O.a/vec2(10,100)), u );
}
