// Top: Correcting the contrast fluctuation due to interpolation.
// ( of course this does not solve aliasing :-) )
// This adds to the gamma problem (Left: final sRGB omitted ) that is even more visible.

// This is valid for random noise. Try other textures (even blue noise) to see the effect of correlation. :-)

void mainImage( out vec4 O, vec2 U )
{
    float t = iMouse.z>0. ? 0. : iTime; // click for 1-1 mapping
    vec2 R = iResolution.xy,
         V = vec2(1.+ .05*sin(t))* (U-.5),
         S = vec2(textureSize(iChannel0,0).xy),
         F = fract(V);
    O = texture(iChannel0, (V+.5)/S).rrrr;

    if (U.y/R.y > .5) {   // Top: corrected contrast
        O -= .5;          // centered noise
        float s2 =        // statistically, this is the variance of interpolated value
                     ( F.x*F.x + (1.-F.x)*(1.-F.x) )
                   * ( F.y*F.y + (1.-F.y)*(1.-F.y) );
        O /= sqrt(s2);
      //O *= .75*.75;
        O += .5;          // back to correct centering
    }
    if (U.x/R.x > .5)     // Right: with sRGB convertion
        O = pow( O, vec4(1./2.2) );

    if (U.y-.5==R.y/2.) O = vec4(1,0,0,1);
    if (U.x-.5==R.x/2.) O = vec4(1,0,0,1);

}
