void mainImage( out vec4 o, in vec2 U )
{
    vec3 d=vec3(U/iResolution.xy-.5*sin(iTime)-.5,.5),
        p=vec3(9.5+1.4*sin(iTime),9.-4.*cos(iTime),iTime+.3),q;
    for(int i=0;++i<99;i==94?q=p+=d=vec3(.6):q)
        p+=d*(length(sin(p)+cos(p*.5))-.4);
    ivec3 u=ivec3(q*5e2);
    o=vec4(u.y&150,u.x&99,u.z&99,0)/1e3*length(p-q)+(p.z-iTime)*.03;
}
