//                      +-------+
//
//
//
//
//
//+--------------------+         +-----------------+
//                      a         b
//function from Texturing and Modeling a procedural approach 3rd
#define PULSE(a,b,x) step(a,x)-step(b,x)
vec3 brick =vec3(0.7,0.,0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 參數化
    float thickness =5.;
    float brick_width=200.;
    float brick_height=60.;

    float y_grid_size = thickness+brick_height;
    float local_y =mod(fragCoord.y,  y_grid_size);
    float v1 =PULSE(0.,thickness,local_y);


    // 黑白相間
    float index_y = floor(fragCoord.y/y_grid_size);
    float black_or_white =mod(index_y ,2.);
    float offset = black_or_white*0.5*brick_width;

    float x_grid_size = thickness+brick_width;
    float local_x =mod(fragCoord.x+offset,  x_grid_size);
    float v2 =PULSE(0.,thickness,local_x);

    float mask =1.- min(v2+v1,1.); // reverse
    vec3 final = brick*mask;


    fragColor = vec4(final,1.0);
}
