void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5f;
    uv.x *= iResolution.x / iResolution.y;		// compensate

    float d = length( uv );
	float s = smoothstep( 0.3 , 0.3-0.01 , d );


    fragColor = vec4(vec3(s),1.0);
}
