// warholizer - some weird halftone-ish experiment

// ...slightly golfed down - guess theres still much potential to get it even smaller

#define R (iResolution.xy)
#define R0 vec2(textureSize(iChannel0,0))

void mainImage( out vec4 c, vec2 p )
{
    float s=0.,f=1.,n=iMouse.x/R.x*64.;
    float m=floor(iMouse.y/R.y*5.+1.);
    if (iMouse.x<1.) { n=2.; m=floor(mod(iTime*.5,3.)+1.); }
    vec2 q=floor(p/(R/m));
    p=mod(p*m,R);
    vec4 dc=vec4(0);
    for(float i=3.;i>=0.;i-=1.)
    {
        c=textureLod(iChannel0,p/R,i+n/15.+log2(R0.x/800.));
        dc+=f*mix(c,vec4(-.1),dot(c.xyz,vec3(-1,2,-1)));
        s+=f;
        f*=.35;
    }
    dc/=s;
    vec4 tc=abs(fract(dc*dc*n+.13*(q.x+m*q.y))*2.-1.);
    vec4 fw=fwidth(tc)*1.;
    c=1.-smoothstep(dc-fw,dc+fw,tc);
}
