void mainImage( out vec4 O, vec2 u )
{ 
    vec2  R = iResolution.xy,
          U = u - .5 *R;

    float l = length(U)/R.y,
          a = atan(U.x , U.y ),
          s = 8.*( a/6.2831  + iTime*.1 + l*2. );

    float m = min( fract(s) , fract(1.-s) ),
          v = m * .3 + .1 - l;

    O = vec4(smoothstep( -.7, .7 , v/fwidth(v) ) );	
}
