float waveRing(float radius, float width, float num, float time, vec2 pos)
{
    radius = radius + 1./(radius*10. +20.)*0.3*sin(5.*time)*sin(num*atan(pos.x,pos.y) + time);
    float t = width / abs(radius - length(pos));
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy *0.5)/ min(iResolution.x,iResolution.y);


    // Time varying pixel color
	// float t = 0.01 / abs(0.2-0.2*fract(5.*(length(uv)+0.01*length(uv)*5.*sin(10.*iTime)*sin(10.*atan(uv.x,uv.y)+iTime))));

    float t = waveRing(0.2,0.01,11.,7.*iTime,uv);

    t += waveRing(0.4,0.01,13.,-5.*iTime,uv);
    t += waveRing(0.6,0.01,17.,3.*iTime,uv);
    t += waveRing(0.8,0.01,19.,-2.*iTime,uv);

    // Output to screen
    fragColor = vec4(vec3(t),1.0);
}
