void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vFontSize = vec2(8.0, 15.0);
    float pi = 3.147;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.,0.,0.);

    float funcOfX = (sin(uv.x * 5. * pi) + 3.) / 3.;
    float blueX = (sin(uv.x * 5. * pi + pi) + 3.) / 3.;

    funcOfX *= 0.5;
    blueX *= 0.5;

    col = vec3(smoothstep(.005,0.,abs(uv.y-funcOfX)),0,0);

    col = mix(col, vec3(1,1,1), vec3(0,0,smoothstep(.005,0.,abs(uv.y - blueX))));

    float timeBetween01 = mod(iTime, 1.);

    if (uv.x > (timeBetween01 * 2.) || uv.x < (timeBetween01 * 2.) - 0.5) {
     	col = vec3(0.,0.,0.);   
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
