void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (( 2.*fragCoord - iResolution.xy ) / iResolution.xy);
    vec3 col = texture(iChannel1,uv.xy+.5-sin(iTime/2.)/10.,.0).rgb;


    uv.x = .01 - abs(uv.x);
    uv.y = .01 - abs(uv.y);
	vec2 uv2=uv+.5;
	float len = length(uv)+.4;
    float len2= length(uv2)+.5;

 	float ring =smoothstep(80.0*(sin(iTime*4.3)+1.1)/3./iResolution.y, 0., abs(len-1.1+(sin(iTime))/2.2));

    //to see how this is constructed, change ring*=  to ring+=  below
    ring*= smoothstep(80.0*(cos(iTime*3.1)+1.5)/3./iResolution.y, -0., abs(len2-(sin((.1))+1.)/3.-.5));


    col += vec3(ring)+.0;

    fragColor = vec4((col),1.0);
}
