//----------------------------------------------------------------------------------------
//  2 out, 1 in...
vec2 hash21(float p)
{
    //from David Hoskins' "Hash without Sine"
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 fract1(vec2 a){
    return (abs(fract(a*2.0+10.0*hash21(floor(iTime/5.0)))-.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (fragCoord*5.0-iResolution.xy)/iResolution.y/10.0;
        uv += vec2(iTime/2.0,iTime/3.0)/8.0;
        t = iTime+float(c)/10.;
        float scale = 4.0;
        float scale1 = 1.7;
        //uv = fract(uv/scale);

        for(int i=0;i<9;i++)
        {
            uv = fract1(uv/scale1)+fract1(uv/scale/2.0);
            //scale *= 1.0+fract((uv.x+uv.y)/50.0)/1000.0;
            uv=fract(-uv/(2.5+(-fract(uv.x+uv.y)))+(uv.yx/(2.0))/scale)*scale/1.5+scale1*scale;
            uv /= scale1;

            uv=uv.yx+col.xy;
            //col = fract(col.yzx);
        }
     col[c] = fract((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}
