void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = 3.*(2.*fragCoord-iResolution.xy)/iResolution.y;
         u = -u.yx;
         u += vec2(1.,0.);
    vec2 v = u;
         u = u/dot(u,u);

    float t = 8.0;
    if(iMouse.z > 0.) t = 10.*iMouse.x/iResolution.x;

    float pi = 3.14159265359;
    vec4 sum = vec4(0.);
    for(float i = 0.; i < t; ++i)
    {
        u /= cos(u.y-vec2(.0,.5*pi))*exp(sin(u.x+iTime));
        u+= v;
        sum+= (1.0+.5*cos(i*4.+vec4(0,1,2,0)))/length(u);
    }
    fragColor = sqrt(sum)*.25;
}
