bool yee (vec3 p)
{
 	if(p.y < sin(p.x) * cos(p.z))
    {
        return true;
    }

    if (abs(p.y-2.)<.1 && sin(p.x)*sin(p.z)>0.1)
        return true;

    return false;
}

bool shadow (vec3 cp, vec3 ld)
{
    vec3 p = cp;
    vec3 d = ld;

    for(int i =0; i < 500; i++)
    {
        p += d/100.;
        if(yee(p))
        {
            return true;
        }
    }
    return false;
}

vec3 marchNotTheMonth(vec3 s, vec3 d)
{
    vec3 p = s;

    for(int i =0; i < 1000; i++)
    {
        p += d/200.;
        if(yee(p))
        {
            vec2 texUv = p.xz;
            bool s = shadow(p,vec3(1,1,0));
            if (s)
                return vec3(texture(iChannel0,texUv)) / 3.;
            return vec3(texture(iChannel0,texUv));
        }
    }

    return vec3(texture(iChannel1,d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 yo = uv*2.-1.;
    vec3 dir = vec3(yo,1);
    vec3 camera = vec3(1,1,iTime);
    vec3 march =  marchNotTheMonth(camera,dir);

    // Time varying pixel color
    vec3 col = vec3(march);


    // Output to screen
    fragColor = vec4(col,1.0);
}
