void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 xy = fragCoord.xy / iResolution.xy; //Поєднуємо це в один рядок
 vec4 texColor = texture(iChannel0,xy); //Отримуємо від iChannel0 піксель в
//координаті xy
texColor.r = (xy.y + xy.x)/2.0;
 fragColor = texColor;// Присвоюємо екранному пікселю цей колір
}
