#define dScene(p) texture(iChannel0, p.xz).x
#define EDGE vec2(1.55, 0.85)

vec3 calcNormal( in vec3 p )
{
    const float eps = 0.008;             

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*(p.y - dScene( (p + v1*eps) )) + 
					  v2*(p.y - dScene( (p + v2*eps) )) + 
					  v3*(p.y - dScene( (p + v3*eps) )) + 
					  v4*(p.y - dScene( (p + v4*eps) )) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec2 uv = fragCoord/iResolution.xy;
    //took a quick shadow method temporarily from this shader: https://www.shadertoy.com/view/XdSyz1
    vec2 uv2 = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    if (abs(uv2.x) > EDGE.x || abs(uv2.y) > EDGE.y) {
    	vec2 delta = 6.0 * clamp(abs(vec2(1.06, 1.1) * uv2 + vec2(-0.04, 0.02)) - EDGE, 0.0, 1.0);
        float shadow = mix(0.55, 1.0, pow(length(delta), 0.7));
        fragColor.rgb = vec3(shadow);
    } else {
        float height = texture(iChannel0, uv).x;
    	vec3 norm = calcNormal(vec3(uv.x, height, uv.y));
    	vec3 col = sqrt(norm.xyz * norm.zxy);
    	float dif1 = smoothstep(0.1, 0.2, col.b);
    	float dif2 = smoothstep(0.1, 0.4, col.r);
    	float dif3 = smoothstep(0.1, 0.2, col.g);
    	vec3 canvas = vec3(226.0, 209.0, 162.0)/256.0;
    	col = dif1 * (1.2 * canvas);   
    	//col = mix(vec3(1.0, 0.0, 0.0), col, dif2);
    	//col = mix(vec3(0.0, 0.0, 1.0), col, dif3);
    	fragColor.rgb = col;
    }
}
