void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = fragCoord/50.;

    float g = 0.;
    vec2 d = vec2(1,0);
    for(float i = 1.;i++<=64.;)
    {
        //Use precomputed kernels here?
        d = cos(cos(i*i-i)*246.+vec2(0,1.57));
        //Or just rotate?
        //d *= mat2(.8,.6,-.6,.8);
        g += cos(dot(c++,d*sqrt(i))+iTime)/i;
    }

    vec3 col = vec3(g/4.+.5);

    fragColor = vec4(col,1);
}
