void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float topThreshold = 1.;
    float bottomThreshold = topThreshold - (sin(iTime)+1.)/2.;
    vec2 topUv = vec2(uv.x, topThreshold);
    vec2 bottomUv = vec2(uv.x, bottomThreshold);

    if (uv.y < topThreshold && uv.y > bottomThreshold) {
        float fac = (uv.y - bottomThreshold) / (topThreshold - bottomThreshold);
        fragColor = mix(
            texture(iChannel0, bottomUv),
            texture(iChannel0, topUv),
            fac);
    } else {
        fragColor = texture(iChannel0, uv);
    }
    //fragColor = texture(iChannel0, vec2(uv.x, 1));
    //fragColor = vec4(mix(topUv, bottomUv, uv.y), 0, 1);
    //fragColor = vec4(uv.y, 0, 0, 1);

}
