void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).xyz;
	vec2 mse = iMouse.xy/iResolution.xy;
    mse *=2.;


    float toonFactor = mse.x;

    col.r = round(col.r/mse.x) * toonFactor;
    col.g = round(col.g/mse.y) * toonFactor;
    col.b = round(col.b/(1.-mse.x)) * toonFactor;

    // Output to screen
    fragColor = vec4(col,1.0);
}
