float circle(in vec2 p)
{
	return length(p) + 0.25;
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float rand(vec2 n) 
{ 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p)
{
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
	vec2 p = ( 2. * fragCoord - res ) / res.y;

    float c = circle(p);
    float d = smoothstep(0.98, 0.99, c);
    vec3 col = vec3(d-0.1);

    // fake diffuse
    col += mix(vec3(smoothstep(c, 0.01, 0.15)*0.25), col, p.x * (1.0-d));

    // striatures
	vec2 pl = p;
    pl.y*=100.0;
    pl.y +=-0.5 + noise(iTime+pl*3.51)*0.532 + noise(iTime+pl*7.0)*1.065 + noise(iTime+pl*14.0)*2.13+sin(p.x*4.0)*2.0;
    float l = sdLine(pl, vec2(-0.9, 0.0), vec2(1.1, -0.25));

    // grainy glow
    vec2 q = p; q*=10.52;
    float g = noise(0.1*iTime+q*160.01);
    g*=1.12; g+= noise(iTime+q*80.02)*0.02;

    col = mix(col, vec3(g), 1.0-l*(1.0-c));

    // Output to screen
    fragColor = vec4(col,1.0);
}
