//the noise is confined within a circle, though it may not look like it
//try chaning circle size to see



float rand(float x){
	return fract(sin(x*17.2323)*538.45);
}
float circle(vec2 p,vec2 center){

    vec2 dist = p-center;
    float d = length(dist);
	return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float i = floor(uv.x);
    float f = fract(uv.x);
	uv-=.5;
    //uv.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(.6,.6,.6);
    col.r += uv.x;
	col.b += uv.y;

    float d = circle(uv,vec2(0.));


    d/=rand(uv.x*((iTime+1000.)*.001))*.1;

    vec3 lit = vec3(smoothstep(10.,.1,d));
    col *= lit;
    fragColor = vec4(col,1.);
}
