// Author Reva - 2020-03-03

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Value noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}
mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

float lines(in vec2 pos, float b){
    return smoothstep(0.0,
                      b*fract(iTime*0.6)*4.0,
                      abs((sin(pos.x*noise(vec2(abs(pos.x)-iTime*0.3,pos.y*(mod(iTime,100.0)+700.0)*0.024)))))*0.900);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 R = iResolution.xy;
    vec2 pos = 10.*(fragCoord - .5*R)/R.y;
    pos *= rotate2d( noise(pos)*.248 );

    float pattern = lines(pos,.5);  
    float col = mix(pattern, 1. - pattern, step(3.,mod(iTime,6.)));

    fragColor = vec4(col);
}
