void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy) /iResolution.y;
	float aspect = iResolution.x/iResolution.y;
    float betrag = length(vec2(uv.x,uv.y));
    float winkel = atan(uv.x,uv.y);
    float r = abs(sin((iTime*0.25+winkel)*10.+cos((betrag+iTime)*5.)*2.));
    float g = sin(winkel*10.+cos((betrag-iTime)*5.)*2.);
    float b = abs(sin((r*3.14)/(1.5+sin(iTime))));


    // Output to screen
    fragColor = vec4(0.7*r,g,1.2*b,1.0);
}

