void mainImage(out vec4 fragColor, in vec2 fragCoord) {   
    vec3 col = vec3(texelFetch(iChannel0, ivec2(fragCoord.xy), 0).xyz);  

    // Output to screen
    fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 prevCol = vec3(texelFetch(iChannel0, ivec2(fragCoord.xy), 0).xyz);

    vec2 ult = iResolution.xx;
    vec2 uv = fragCoord / ult;
    vec2 mouse = iMouse.xy / ult;
    vec2 uvm = (uv - mouse) * 30.; // higher 30 is, smaller the brush
    vec3 timeCol = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    vec3 col = vec3(floor(length(uvm)));

    vec3 finalCol = mix(timeCol, prevCol, col);

    fragColor = vec4(finalCol, 1.0);
}
